sourcesContent:
const userService = require("../services/user-service");
const companyService = require("../services/company-service");
const jwt = require("jsonwebtoken");

const userController = {
  register: async (req, res) => {
    try {
      const { user, password, company, rol } = req.body;
      const data = {
        user,
        password,
        rol: rol || "user",
      };
      // usuariosCollection.insertOne(data);
      const companyE = await companyService.getCompany(company);
      if (!companyE.length) {
        res.status(404).json({ status: false, msg: "No existe la empresa" });
        return;
      }
      const result = await userService.createUser(data);
      if (result.insertedId) {
        const user = await userService.getUserById(result.insertedId);
        const companyData = {
          company: companyE[0]._id,
          user: user[0]._id,
        };
        const aux = await companyService.addUser(companyData);
        if (aux.modifiedCount === 0) {
          throw new Error("Error al agregar el usuario a la empresa");
        }
        return res
          .status(200)
          .json({ status: true, msg: "Usuario creado con éxito", data: data });
      }
      throw new Error("Error al crear el usuario");
    } catch (error) {
      console.log(error);
      res.status(500).json({ status: false, msg: error.message });
    }
  },
  login: async (req, res) => {
    try {
      const result = await userService.getUser(req.body);

      if (result.length) {
        const { _id, user, rol } = result[0];
        const exist_in_company = await companyService.getCompanyByUser({
          user: _id,
        });
        if (!exist_in_company.length && rol != "superadmin") {
          throw new Error("El usuario no pertenece a ninguna empresa");
        }
        data = { user, rol, company: exist_in_company[0]?._id || "" };
        const token = jwt.sign(data, "secret_key", {
          expiresIn: "7d",
        });
        res.status(200).json({
          status: true,
          msg: "Inicio de sesión exitoso!",
          data,
          token: token,
        });
        return;
      }
      res.status(404).json({
        status: true,
        msg: "Credenciales incorrectas..!",
        data: "",
        token: "",
      });
    } catch (error) {
      console.log(error);
      res.status(500).json({
        status: true,
        msg: "Error en el servidor..!",
        data: "",
        token: "",
      });
    }
  },
  getAllUsers: async (req, res) => {
    try {
      const result = await userService.getAllUsers();
      res.status(200).json({ status: true, msg: "success", data: result });
    } catch (error) {
      console.log(error);
      res.status(500).json({ status: false, msg: error.message });
    }
  },
  getAllUsersByCompany: async (req, res) => {
    try {
      const { company } = req.user;
      const result = await userService.getCompany(company);
      if (!result.length) {
        throw new Error("No se encontró la empresa");
      }
      const users = await userService.getUsersByCompany(company);
      res.status(200).json({ status: true, msg: "success", data: users });
    } catch (error) {
      console.log(error);
      res.status(500).json({ status: false, msg: error.message });
    }
  },
  changePassword: async (req, res) => {
    try {
      const { id, password } = req.body;
      const exist = await userService.getUserById(id);
      if (!exist.length) {
        throw new Error("No se encontró el usuario");
      }
      const result = await userService.changePassword(id, password);
      if (result.modifiedCount) {
        res.status(200).json({ status: true, msg: "success", data: result });
        return;
      }
      throw new Error("Error al cambiar la contraseña");
    } catch (error) {
      console.log(error);
      res.status(500).json({ status: false, msg: error.message });
    }
  },
  updateUser: async (req, res) => {
    try {
      const { id, password, rol } = req.body;
      const exist = await userService.getUserById(id);
      if (!exist.length) {
        throw new Error("No se encontró el usuario");
      }
      const result = await userService.updateUser(id, password, rol);
      if (result.matchedCount) {
        res.status(200).json({
          status: true,
          msg: "Usuario actualizado con éxito",
          data: result,
        });
        return;
      }
      throw new Error("Error al actualizar el usuario");
    } catch (error) {
      console.log(error);
      res.status(500).json({ status: false, msg: error.message });
    }
  },
};

module.exports = userController;

